CREATE DATABASE GestionLibros
GO
USE GestionLibros;
CREATE TABLE Categorias(
	IdCategoria INTEGER IDENTITY PRIMARY KEY,
	NombreCategoria VARCHAR(100) NOT NULL,
	Descripcion VARCHAR(200),--NO ES NECESARIA
	Alias VARCHAR(100),
	CONSTRAINT UC_Categorias UNIQUE (NombreCategoria),
	CONSTRAINT UC_Categorias_Id UNIQUE (IdCategoria)
);

CREATE TABLE SubCategorias(
	IdSubCategoria INTEGER IDENTITY PRIMARY KEY,
	NombreSubCategoria VARCHAR(100) NOT NULL,
	Descripcion VARCHAR(200) NOT NULL,
	IdCategoria INTEGER NOT NULL,
	Alias VARCHAR(100),
	CONSTRAINT UC_SubCategorias UNIQUE (NombreSubCategoria),
	CONSTRAINT UC_SubCategorias_Id UNIQUE (IdSubcategoria),
	CONSTRAINT Fk_IdSubcategoria FOREIGN KEY (IdCategoria) REFERENCES Categorias(IdCategoria) ON DELETE CASCADE ON UPDATE CASCADE
);



CREATE TABLE Paises(
   IdPais INTEGER IDENTITY PRIMARY KEY,
   NombrePais VARCHAR(100) NOT NULL,
   CONSTRAINT UC_Paises_Id UNIQUE (IdPais)
);

CREATE TABLE Provincias (
  IdProvincia VARCHAR(200) PRIMARY KEY,
  NombreProvincia VARCHAR(100) NOT NULL,
  ProvinciaSeo VARCHAR(100) NOT NULL,
  AbreviaturaProvincia CHAR(3) NOT NULL,
  IdPais INTEGER,
  CONSTRAINT Fk_IdPais_Provincia FOREIGN KEY (IdPais) REFERENCES Paises(IdPais) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT UC_Provincias UNIQUE (NombreProvincia),
  CONSTRAINT UC_Provincias_Id UNIQUE (IdProvincia)
);

CREATE TABLE Ciudades(
	IdCiudad INTEGER IDENTITY PRIMARY KEY,
	IdProvincia VARCHAR(200) NOT NULL,
	CodigoCiudad VARCHAR(200) NOT NULL,
	NombreCiudad VARCHAR(200) NOT NULL,
	CONSTRAINT Fk_IdCiudad_Provincia FOREIGN KEY (IdProvincia) REFERENCES Provincias(IdProvincia) ON UPDATE CASCADE,
	CONSTRAINT UC_Ciudad_Id UNIQUE (IdCiudad)
);


CREATE TABLE Editoriales (
    IdEditorial INTEGER IDENTITY PRIMARY KEY,
    Nombre  VARCHAR(100) NOT NULL,
    Direccion VARCHAR(100) NOT NULL,
    IdCiudad INTEGER NOT NULL,
    Telefono CHAR(9) NOT NULL,
    Fax VARCHAR(9) NOT NULL,
    Email VARCHAR(100) NOT NULL,
    CONSTRAINT Fk_IdCiudad_Editoriales FOREIGN KEY (IdCiudad) REFERENCES Ciudades(IdCiudad)  ON UPDATE CASCADE,
    CONSTRAINT CH_TelefonoEditorial_Restriccion CHECK (Telefono like '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
    CONSTRAINT UC_Editorial_Id UNIQUE (IdEditorial),
    CONSTRAINT UC_Editorial_Nombre UNIQUE (Nombre)
);

CREATE TABLE Autores (
	IdAutor INTEGER IDENTITY PRIMARY KEY,
	Nombre  VARCHAR(100) NOT NULL,
	Nacionalidad VARCHAR(100) NOT NULL,
	IdPais INTEGER NOT NULL,
	CONSTRAINT Fk_IdPais_Autores FOREIGN KEY (IdPais) REFERENCES Paises(IdPais) ON DELETE CASCADE ON UPDATE CASCADE
	
);



CREATE TABLE Libros(
	IdLibro INTEGER IDENTITY PRIMARY KEY,
	IdSubCategoria INTEGER,
	Titulo VARCHAR(200) NOT NULL,		
	IdAutor INTEGER ,			
	Descripcion NVARCHAR(500) NOT NULL,	
	CONSTRAINT Fk_Autor_Libro FOREIGN KEY (IdAutor) REFERENCES Autores(IdAutor) ON DELETE SET NULL,
	
	CONSTRAINT FK_BorrarLibros_SubCategoria FOREIGN KEY (IdSubCategoria) REFERENCES SubCategorias(IdSubCategoria) ON DELETE SET NULL
);


CREATE TABLE Ejemplares(
	IdEjemplar INTEGER IDENTITY PRIMARY KEY,
	NumeroEdicion INTEGER NOT NULL,
	Encuadernacion VARCHAR(200) NOT NULL,
	FechaDisponibilidad DATETIME NOT NULL,
	YearPublicacion CHAR(4) NOT NULL,
	Formato VARCHAR(50) NOT NULL,	
	IdEditorial INTEGER,
	Idioma VARCHAR(50) NOT NULL,
	ISBN CHAR(40) NOT NULL,
	Paginas INTEGER NOT NULL,
	PVP MONEY NOT NULL,
	Portada VARCHAR(1000),
	
	IdLibro INTEGER NOT NULL,
	CONSTRAINT Fk_Ejemplares_Libro FOREIGN KEY (IdLibro) REFERENCES Libros(IdLibro) ON DELETE CASCADE ON UPDATE CASCADE,
	CONSTRAINT Fk_Editorial_Ejemplares FOREIGN KEY (IdEditorial) REFERENCES Editoriales(IdEditorial) ON DELETE SET NULL,
	CONSTRAINT UC_Libros_ISBN UNIQUE (ISBN)
);




CREATE TABLE Usuario(
	Usuario VARCHAR(100) PRIMARY KEY,
	Contrasenia VARCHAR(150) NOT NULL,
	IdGrupoUsuario INTEGER NOT NULL,
	Nombre VARCHAR(30) NOT NULL,
	PrimerApellido VARCHAR(30) NOT NULL,
	SegundoApellido VARCHAR(30) NOT NULL,
	IdCiudad INTEGER NOT NULL,
	Telefono CHAR(9) NOT NULL,
	Direccion VARCHAR(100) NOT NULL,
	Nif CHAR(9) NOT NULL,
	Fax VARCHAR(100) ,
	Email VARCHAR(100) NOT NULL,
	Activo BIT DEFAULT 1,
	CONSTRAINT CH_Usuario_Restriccion_Telefono CHECK (Telefono like '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
    CONSTRAINT Fk_Id_Usuario_Ciudad FOREIGN KEY (IdCiudad) REFERENCES Ciudades(IdCiudad) ON DELETE CASCADE ON UPDATE CASCADE,
	CONSTRAINT UC_Usuario_NIF UNIQUE (Nif),
	CONSTRAINT UC_Usuario_NickUsuario UNIQUE (Usuario),
	CONSTRAINT CH_Usuario_Restriccion_Grupo CHECK (IdGrupoUsuario like 1 OR IdGrupoUsuario LIKE 2 OR IdGrupoUsuario LIKE 3)
);

CREATE TABLE Tiendas(
	IdTienda INTEGER IDENTITY PRIMARY KEY,
	Nombre VARCHAR(50) NOT NULL,
	IdCiudad INTEGER NOT NULL,
	Telefono CHAR(9) NOT NULL,
	Direccion VARCHAR(200) NOT NULL,
	ImagenTienda VARCHAR(200) NOT NULL,
	Activo BIT NOT NULL,
	CONSTRAINT Fk_Id_Ciudad FOREIGN KEY (IdCiudad) REFERENCES Ciudades(IdCiudad) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE AdministradorTienda(
	Usuario VARCHAR(100) PRIMARY KEY,
	Contrasenia VARCHAR(150) NOT NULL,
	IdGrupoUsuario INTEGER NOT NULL,
	Nombre VARCHAR(30) NOT NULL,
	PrimerApellido VARCHAR(30) NOT NULL,
	SegundoApellido VARCHAR(30) NOT NULL,
	IdCiudad INTEGER NOT NULL,
	Telefono CHAR(9) NOT NULL,
	Direccion VARCHAR(200) NOT NULL,
	Nif CHAR(9) NOT NULL,
	Fax VARCHAR(100) ,
	Email VARCHAR(100) NOT NULL,
	IdTienda INTEGER NOT NULL,
	Activo BIT DEFAULT 1,
	CONSTRAINT CH_AdministradorTienda_Restriccion_Telefono CHECK (Telefono like '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
	CONSTRAINT CH_AdministradorTienda_Tienda FOREIGN KEY (IdTienda) REFERENCES Tiendas(IdTienda),
    CONSTRAINT Fk_Id_AdministradorTienda_Ciudad FOREIGN KEY (IdCiudad) REFERENCES Ciudades(IdCiudad) ON DELETE CASCADE ON UPDATE CASCADE,
	CONSTRAINT UC_AdministradorTienda_NIF UNIQUE (Nif),
	CONSTRAINT UC_AdministradorTienda_NickUsuario UNIQUE (Usuario),
	CONSTRAINT CH_AdministradorTienda_Restriccion_Grupo CHECK (IdGrupoUsuario like 1 OR IdGrupoUsuario LIKE 2 OR IdGrupoUsuario LIKE 3)
);

CREATE TABLE SuperAdministrador(
	Usuario VARCHAR(100) PRIMARY KEY,
	Contrasenia VARCHAR(150) NOT NULL,
	IdGrupoUsuario INTEGER NOT NULL,
	Nombre VARCHAR(30) NOT NULL,
	PrimerApellido VARCHAR(30) NOT NULL,
	SegundoApellido VARCHAR(30) NOT NULL,
	IdCiudad INTEGER NOT NULL,
	Telefono CHAR(9) NOT NULL,
	Direccion VARCHAR(200) NOT NULL,
	Nif CHAR(9) NOT NULL,
	Fax VARCHAR(100) ,
	Email VARCHAR(100) NOT NULL,
	Activo BIT DEFAULT 1,
	CONSTRAINT CH_SuperAdministrador_Restriccion_Telefono CHECK (Telefono like '[0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9][0-9]'),
	CONSTRAINT Fk_Id_SuperAdministrador_Ciudad FOREIGN KEY (IdCiudad) REFERENCES Ciudades(IdCiudad) ON DELETE CASCADE ON UPDATE CASCADE,
	CONSTRAINT UC_SuperAdministrador_NIF UNIQUE (Nif),
	CONSTRAINT UC_SuperAdministrador_NickUsuario UNIQUE (Usuario),
	CONSTRAINT CH_SuperAdministrador_Restriccion_Grupo CHECK (IdGrupoUsuario like 1 OR IdGrupoUsuario LIKE 2 OR IdGrupoUsuario LIKE 3)
);



CREATE TABLE Almacen(
	IdEjemplar INTEGER NOT NULL,
	IdTienda INTEGER NOT NULL,
	Stock INTEGER NOT NULL,
	CONSTRAINT FK_Id_EjemplaresAlmacen FOREIGN KEY (IdEjemplar) REFERENCES Ejemplares(IdEjemplar) ON DELETE CASCADE ON UPDATE CASCADE,
	CONSTRAINT FK_ID_Tienda_Almacen FOREIGN KEY (IdTienda) REFERENCES Tiendas(IdTienda) 
);


CREATE TABLE Venta
(
	IdVenta INTEGER IDENTITY PRIMARY KEY,
	Total MONEY NOT NULL,
	FechaCompra DATETIME NOT NULL,
	Usuario VARCHAR(100) NOT NULL,	
	CONSTRAINT Fk_Venta_Usuario FOREIGN KEY (Usuario) REFERENCES Usuario(Usuario) ON UPDATE CASCADE ON DELETE CASCADE	
);

CREATE TABLE DetalleVenta 
(
	IdVenta INTEGER NOT NULL,
	IdEjemplar INTEGER NOT NULL,
	Cantidad INTEGER NOT NULL,
	PVP MONEY NOT NULL,
	IdTienda INTEGER,
	Activo BIT DEFAULT 1,
	Nulo BIT DEFAULT 1,
	CONSTRAINT Fk_DetalleVenta_Ejemplar  FOREIGN KEY (IdEjemplar) REFERENCES Ejemplares(IdEjemplar),
	CONSTRAINT Fk_DetalleVenta_IdCompra FOREIGN KEY (IdVenta) REFERENCES Venta(IdVenta)  ON DELETE CASCADE ON UPDATE CASCADE
	,CONSTRAINT Fk_Compra_IdTienda FOREIGN KEY (IdTienda) REFERENCES Tiendas(IdTienda)

);

