 CREATE PROCEDURE findUsuario
(
	@eCorreo nvarchar(100),
	@pass nvarchar(500) 
)
AS
BEGIN
	SET NOCOUNT ON;

	IF EXISTS
		(SELECT * FROM Usuario WHERE Email = @eCorreo AND Contrasenia = @pass)
	BEGIN
		SELECT  3 as tipo FROM Usuario WHERE Email = @eCorreo AND Contrasenia = @pass;
	END
	ELSE IF EXISTS (SELECT * FROM AdministradorTienda WHERE Email = @eCorreo AND Contrasenia = @pass)
	BEGIN
		SELECT  2 as tipo FROM AdministradorTienda WHERE Email = @eCorreo AND Contrasenia = @pass;
	END
	ELSE IF EXISTS (SELECT * FROM SuperAdministrador WHERE Email = @eCorreo AND Contrasenia = @pass)
	BEGIN
		SELECT  1 as tipo FROM SuperAdministrador WHERE Email = @eCorreo AND Contrasenia = @pass;
	END
	
END;
