CREATE PROCEDURE devolverLibrosReservadosAlALmacen
(
	@stock INT,
	@idTienda INT,
	@idEjemplar INT 
)
AS BEGIN
	UPDATE Almacen SET stock =  (stock + @stock) WHERE idTienda = @idTienda AND idEjemplar = @idEjemplar;
END	

